INSERT INTO numerical (category, type, sub_type, instruction, question, correct_answer, wrong_answer1, wrong_answer2, wrong_answer3, explanation) VALUES
('Arithmetic Fundamentals', 'Decimals', 'Addition of decimals', 'Add the decimals.', 'What is 12.45 + 3.678?', '16.128', '16.123', '15.128', '16.138', 'Align decimal points and add: 12.450 + 3.678 = 16.128.'),
('Arithmetic Fundamentals', 'Decimals', 'Subtraction of decimals', 'Subtract the decimals.', 'Calculate 8.05 - 3.678', '4.372', '4.427', '5.372', '4.362', 'Align decimal points and subtract: 8.050 - 3.678 = 4.372.'),
('Arithmetic Fundamentals', 'Decimals', 'Multiplication of decimals', 'Multiply the decimals.', 'What is 2.4 * 1.35?', '3.24', '3.25', '3.4', '3.2', 'Multiply as whole numbers (240 * 135 = 32400), then place 3 decimal digits: 3.24.'),
('Arithmetic Fundamentals', 'Decimals', 'Division of decimals', 'Divide the decimals.', 'What is 6.75 / 0.5?', '13.5', '1.35', '12.5', '13.25', 'Dividing by 0.5 is the same as multiplying by 2: 6.75 * 2 = 13.5.'),
('Arithmetic Fundamentals', 'Decimals', 'Converting decimals to fractions', 'Convert the decimal to a simplified fraction.', 'What is 0.875 as a fraction?', '\\(\\frac{7}{8}\\)', '\\(\\frac{8}{7}\\)', '\\(\\frac{87}{100}\\)', '\\(\\frac{875}{1000}\\)', '0.875 = 875/1000 → simplify by dividing numerator and denominator by 125: 7/8.'),
('Arithmetic Fundamentals', 'Decimals', 'Converting fractions to decimals', 'Convert the fraction to a decimal.', 'What is \\(\\frac{7}{20}\\) as a decimal?', '0.35', '0.7', '0.035', '0.357', '7 ÷ 20 = 0.35.'),
('Arithmetic Fundamentals', 'Decimals', 'Rounding decimals to a given place value', 'Round the decimal to the nearest hundredth.', 'Round 12.6789 to the nearest hundredth.', '12.68', '12.67', '12.7', '12.69', 'Hundredth place is 7; thousandth digit is 8 → round up to 12.68.'),
('Arithmetic Fundamentals', 'Decimals', 'Comparing decimals using place value', 'Compare the decimals using >, <, or =.', 'Which symbol makes this true? 0.305 ? 0.35', '<', '>', '=', '>=', 'Compare hundredths: 0.305 < 0.35.'),
('Arithmetic Fundamentals', 'Decimals', 'Ordering decimals from least to greatest', 'Arrange the decimals in ascending order.', 'Order from least to greatest: 2.45, 2.405, 2.5', '2.405, 2.45, 2.5', '2.45, 2.405, 2.5', '2.405, 2.5, 2.45', '2.5, 2.45, 2.405', 'Compare place values: 2.405 < 2.45 < 2.5.'),
('Arithmetic Fundamentals', 'Decimals', 'Converting between decimals and percentages', 'Convert the decimal to a percentage.', 'What is 0.72 as a percentage?', '72%', '0.72%', '720%', '7.2%', 'Multiply by 100: 0.72 × 100 = 72%.');
