INSERT INTO numerical (category, type, sub_type, instruction, question, correct_answer, wrong_answer1, wrong_answer2, wrong_answer3, explanation) VALUES
('Arithmetic Fundamentals', 'Percentages', 'Calculating percentage of a number', 'Calculate the percentage of the number.', 'What is 25% of 320?', '80', '75', '85', '90', '25% = 0.25. Multiply: 0.25 × 320 = 80.'),
('Arithmetic Fundamentals', 'Percentages', 'Finding the whole given a part and its percentage', 'Find the whole number given a part and its percentage.', 'If 45 is 15% of a number, what is the number?', '300', '250', '270', '320', '15% = 0.15. Whole = 45 ÷ 0.15 = 300.'),
('Arithmetic Fundamentals', 'Percentages', 'Calculating percentage increase', 'Calculate the percentage increase.', 'A price increases from 120 to 150. What is the percentage increase?', '25%', '30%', '20%', '15%', 'Increase = 150 − 120 = 30. 30 ÷ 120 = 0.25 = 25%.'),
('Arithmetic Fundamentals', 'Percentages', 'Calculating percentage decrease', 'Calculate the percentage decrease.', 'A value decreases from 200 to 160. What is the percentage decrease?', '20%', '25%', '15%', '18%', 'Decrease = 200 − 160 = 40. 40 ÷ 200 = 0.20 = 20%.'),
('Arithmetic Fundamentals', 'Percentages', 'Finding discount amount and final price after discount', 'Find the discount amount and final price.', 'An item costs $250 and is discounted by 12%. What is the final price?', '220', '230', '215', '225', 'Discount = 0.12 × 250 = 30. Final price = 250 − 30 = 220.'),
('Arithmetic Fundamentals', 'Percentages', 'Calculating sales tax or VAT', 'Calculate the final price including tax.', 'An item costs $480 with a 7% sales tax. What is the total price?', '513.6', '510', '515', '520', 'Tax = 0.07 × 480 = 33.6. Total = 480 + 33.6 = 513.6.'),
('Arithmetic Fundamentals', 'Percentages', 'Calculating markup and selling price', 'Calculate the selling price given cost and markup percentage.', 'A shop buys a bag for $60 and marks it up by 25%. What is the selling price?', '75', '70', '72', '80', 'Markup = 0.25 × 60 = 15. Selling price = 60 + 15 = 75.'),
('Arithmetic Fundamentals', 'Percentages', 'Finding profit or loss percentage', 'Find the profit or loss percentage.', 'A watch is bought for $150 and sold for $180. What is the profit percentage?', '20%', '25%', '15%', '18%', 'Profit = 180 − 150 = 30. 30 ÷ 150 = 0.20 = 20%.'),
('Arithmetic Fundamentals', 'Percentages', 'Calculating simple interest', 'Calculate the simple interest.', 'Find the simple interest on $2,000 at 5% per year for 3 years.', '300', '250', '350', '280', 'I = P × R × T = 2000 × 0.05 × 3 = 300.'),
('Arithmetic Fundamentals', 'Percentages', 'Solving real-life word problems involving percentages', 'Solve the problem.', 'A student scored 540 out of 600 marks. What percentage did they score?', '90%', '88%', '92%', '89%', 'Percentage = (540 ÷ 600) × 100 = 90%.');
