INSERT INTO numerical (category, type, sub_type, instruction, question, correct_answer, wrong_answer1, wrong_answer2, wrong_answer3, explanation) VALUES
('Arithmetic Fundamentals', 'Percentages', 'Percentage Increase', 'Find the percentage increase between two numbers.', 'The price increased from 200 to 250. Find the percentage increase.', '25%', '20%', '30%', '15%', 'Percentage increase = ((250 - 200)/200) × 100 = 25%.'),
('Arithmetic Fundamentals', 'Percentages', 'Percentage Increase', 'Find the percentage increase between two numbers.', 'The population increased from 5000 to 5500. Find the percentage increase.', '10%', '5%', '15%', '12%', '((5500 - 5000)/5000) × 100 = 10%.'),
('Arithmetic Fundamentals', 'Percentages', 'Percentage Increase', 'Find the percentage increase between two numbers.', 'The salary increased from 1200 to 1320. Find the percentage increase.', '10%', '12%', '8%', '15%', '((1320 - 1200)/1200) × 100 = 10%.'),
('Arithmetic Fundamentals', 'Percentages', 'Percentage Increase', 'Find the percentage increase between two numbers.', 'The price increased from 80 to 100. Find the percentage increase.', '25%', '20%', '30%', '15%', '((100 - 80)/80) × 100 = 25%.'),
('Arithmetic Fundamentals', 'Percentages', 'Percentage Increase', 'Find the percentage increase between two numbers.', 'The number increased from 60 to 75. Find the percentage increase.', '25%', '20%', '30%', '15%', '((75 - 60)/60) × 100 = 25%.');
