INSERT INTO numerical (category, type, sub_type, instruction, question, correct_answer, wrong_answer1, wrong_answer2, wrong_answer3, explanation) VALUES
('Arithmetic Fundamentals', 'Percentages', 'Discounts', 'Find the discounted price after applying the given percentage discount.', 'An item costs 500 and has a 20% discount. What is the discounted price?', '400', '450', '420', '380', 'Discount = 20% of 500 = 100. Price after discount = 500 - 100 = 400.'),
('Arithmetic Fundamentals', 'Percentages', 'Discounts', 'Find the discounted price after applying the given percentage discount.', 'An item costs 800 with a 15% discount. Find the sale price.', '680', '700', '720', '650', 'Discount = 15% of 800 = 120. Sale price = 800 - 120 = 680.'),
('Arithmetic Fundamentals', 'Percentages', 'Discounts', 'Find the discounted price after applying the given percentage discount.', 'A laptop costs 1200 and is on 25% off. Find the discounted price.', '900', '950', '850', '1000', 'Discount = 25% of 1200 = 300. Price after discount = 1200 - 300 = 900.'),
('Arithmetic Fundamentals', 'Percentages', 'Discounts', 'Find the discounted price after applying the given percentage discount.', 'A jacket costs 450 and has a 10% discount. Find the price after discount.', '405', '400', '420', '395', 'Discount = 10% of 450 = 45. Price after discount = 450 - 45 = 405.'),
('Arithmetic Fundamentals', 'Percentages', 'Discounts', 'Find the discounted price after applying the given percentage discount.', 'A phone costs 2500 with a 30% discount. What is the sale price?', '1750', '1800', '1700', '1600', 'Discount = 30% of 2500 = 750. Sale price = 2500 - 750 = 1750.');
INSERT INTO numerical (category, type, sub_type, instruction, question, correct_answer, wrong_answer1, wrong_answer2, wrong_answer3, explanation) VALUES
