INSERT INTO numerical (category, type, sub_type, instruction, question, correct_answer, wrong_answer1, wrong_answer2, wrong_answer3, explanation) VALUES
('Arithmetic Fundamentals', 'Work Problems', 'Combined Work', 'Solve the problem using collaboration / combined work concept.', 'Henry can wash his truck in 3 hours. Jack can wash the same truck in 8 hours. If they start together, how long until the truck is clean?', '2.18 hours', '2 hours', '2.5 hours', '3 hours', 'Together, Henry and Jack finish in about 2.18 hours.'),
('Arithmetic Fundamentals', 'Work Problems', 'Combined Work', 'Solve the problem using collaboration / combined work concept.', 'Alice paints a room in 4 hours alone. Bob takes 6 hours for the same task. If they paint the room at the same time, how long will it take them?', '2.4 hours', '2 hours', '2.5 hours', '3 hours', 'Together, Alice and Bob finish the room in roughly 2.4 hours.'),
('Arithmetic Fundamentals', 'Work Problems', 'Combined Work', 'Solve the problem using collaboration / combined work concept.', 'Tom starts mowing a lawn and works for 2 hours. Then Jerry joins him and they finish together. Tom alone can mow in 6 hours, Jerry in 4 hours. How long do they work together after Jerry joins?', '1.2 hours', '1 hour', '1.5 hours', '2 hours', 'Tom completes 2/6=1/3 of lawn; remaining 2/3 done together: 1/T=1/6+1/4 → T≈1.2 hours.'),
('Arithmetic Fundamentals', 'Work Problems', 'Combined Work', 'Solve the problem using collaboration / combined work concept.', 'Alice can paint a wall in 5 hours. She paints alone for 2 hours. Bob then joins her and can paint the wall in 3 hours alone. How long will they take together to finish the wall?', '1.5 hours', '1 hour', '2 hours', '2.5 hours', 'Alice does 2/5 of wall; remaining 3/5 done together: 1/T=1/5+1/3 → T=1.5 hours.'),
('Arithmetic Fundamentals', 'Work Problems', 'Combined Work', 'Solve the problem using collaboration / combined work concept.', 'Tom, Jerry, and Sam mow a lawn together. Tom alone takes 6 hours, Jerry 8 hours, Sam 12 hours. How long will it take them together?', '2 hours', '1.8 hours', '2.5 hours', '3 hours', 'Combined rate: 1/6+1/8+1/12=1/2 → T=2 hours.'),
('Arithmetic Fundamentals', 'Work Problems', 'Combined Work', 'Solve the problem using collaboration / combined work concept.', 'Three friends wash a car. Alice 3 hours, Bob 6 hours, Carol 2 hours. How long together?', '1 hour', '1.2 hours', '1.5 hours', '1.3 hours', '1/3+1/6+1/2=1 → T=1 hour.'),
('Arithmetic Fundamentals', 'Work Problems', 'Combined Work', 'Solve the problem using collaboration / combined work concept.', 'A worker can complete a task in 5 hours. He completes half the task, then a helper who takes 10 hours alone finishes it. How long does the helper take?', '2.5 hours', '2 hours', '3 hours', '1.5 hours', 'Half task = 1/2, remaining 1/2 by both: 1/T=1/5+1/10 → T=2.5 hours.'),
('Arithmetic Fundamentals', 'Work Problems', 'Combined Work', 'Solve the problem using collaboration / combined work concept.', 'Alice completes 1/3 of a wall in 2 hours. Bob can do the full wall in 4 hours. How long will Bob take to finish the remaining wall?', '2 hours', '1.5 hours', '2.5 hours', '3 hours', 'Alice does 1/3; remaining 2/3 done by Bob alone: 4*(2/4)=2 hours.'),
('Arithmetic Fundamentals', 'Work Problems', 'Combined Work', 'Solve the problem using collaboration / combined work concept.', 'Tom can do a job in 4 hours. Jerry is half as efficient. How long will it take both together?', '2.67 hours', '2.5 hours', '3 hours', '2 hours', 'Jerry takes 8 hours. 1/T=1/4+1/8=3/8 → T=8/3 ≈ 2.67 hours.'),
('Arithmetic Fundamentals', 'Work Problems', 'Combined Work', 'Solve the problem using collaboration / combined work concept.', 'Alice can type a report in 3 hours. Bob types twice as fast. How long will both take together?', '1 hour', '1.2 hours', '1.5 hours', '2 hours', 'Bob takes 1.5 hours. Combined rate: 1/3+1/1.5=1 → T=1 hour.'),
('Arithmetic Fundamentals', 'Work Problems', 'Combined Work', 'Solve the problem using collaboration / combined work concept.', 'Tom can paint a wall in 5 hours. Jerry can paint it in 10 hours. How much of the wall is done in 2 hours if they work together?', '0.6', '0.5', '0.7', '0.55', 'Rate:1/5+1/10=3/10 per hour. 2 hours: 3/10*2=0.6 of wall.'),
('Arithmetic Fundamentals', 'Work Problems', 'Combined Work', 'Solve the problem using collaboration / combined work concept.', 'Alice can finish a task in 4 hours. Bob can do it in 6 hours. How much of the task is completed in 3 hours together?', '1.25', '1', '1.2', '1.3', 'Rate:1/4+1/6=5/12 per hour. 3 hours → 5/12*3=15/12=1.25 tasks.'),
('Arithmetic Fundamentals', 'Work Problems', 'Combined Work', 'Solve the problem using collaboration / combined work concept.', 'A chef can prepare dinner in 2 hours. His assistant takes 3 hours. If they cook together, how long until the dinner is ready?', '1.2 hours', '1 hour', '1.5 hours', '2 hours', 'Together, the chef and assistant finish in about 1.2 hours.'),
('Arithmetic Fundamentals', 'Work Problems', 'Combined Work', 'Solve the problem using collaboration / combined work concept.', 'A gardener can water the garden in 6 hours. His helper can do it in 4 hours. How long will it take them together?', '2.4 hours', '2 hours', '3 hours', '2.5 hours', 'The gardener and helper complete the garden together in roughly 2.4 hours.'),
('Arithmetic Fundamentals', 'Work Problems', 'Combined Work', 'Solve the problem using collaboration / combined work concept.', 'John can clean the garage in 5 hours. Mike can do it in 3 hours. If they work together, how long will it take?', '1.88 hours', '2 hours', '1.75 hours', '2.25 hours', 'Rate:1/5+1/3=8/15 → T=15/8=1.875≈1.88 hours.'),
('Arithmetic Fundamentals', 'Work Problems', 'Combined Work', 'Solve the problem using collaboration / combined work concept.', 'Two friends fill a swimming pool. One can fill it in 7 hours, the other in 5 hours. How long together?', '2.92 hours', '3 hours', '2.5 hours', '3.5 hours', 'Rate:1/7+1/5=12/35 → T=35/12≈2.92 hours.'),
('Arithmetic Fundamentals', 'Work Problems', 'Combined Work', 'Solve the problem using collaboration / combined work concept.', 'Three workers repair a road. A in 6 hrs, B in 8 hrs, C in 12 hrs. How long to complete together?', '2 hours', '2.5 hours', '1.8 hours', '2.2 hours', '1/6+1/8+1/12=1/2 → T=2 hours.');
