INSERT INTO `verbal` 
(`category`, `type`, `sub_type`, `instruction`, `word`, `question`, `correct_answer`, `wrong_answer1`, `wrong_answer2`, `wrong_answer3`, `explanation`) VALUES
('Grammar and Structure', 'Comparative/Superlative Forms', 'Comparative', 'Choose the correct comparative or superlative form of the given adjective or adverb for the sentence.', 'tall', 'My brother is ____ than me.', 'taller', 'tallest', 'more tall', 'most tall', 'The comparative form of "tall" is "taller" for comparing two things.'),
('Grammar and Structure', 'Comparative/Superlative Forms', 'Superlative', 'Choose the correct comparative or superlative form of the given adjective or adverb for the sentence.', 'beautiful', 'She is the ____ woman in the room.', 'most beautiful', 'more beautiful', 'beautifuller', 'beautifulest', 'The superlative form of "beautiful" is "most beautiful" for comparing more than two.'),
('Grammar and Structure', 'Comparative/Superlative Forms', 'Comparative', 'Choose the correct comparative or superlative form of the given adjective or adverb for the sentence.', 'quickly', 'He finished the race ____ than anyone else.', 'more quickly', 'most quickly', 'quicklier', 'quickest', 'The comparative form of the adverb "quickly" is "more quickly".'),
('Grammar and Structure', 'Comparative/Superlative Forms', 'Superlative', 'Choose the correct comparative or superlative form of the given adjective or adverb for the sentence.', 'bad', 'This is the ____ movie I have ever seen.', 'worst', 'worse', 'most bad', 'baddest', 'The superlative form of "bad" is "worst".'),
('Grammar and Structure', 'Comparative/Superlative Forms', 'Comparative', 'Choose the correct comparative or superlative form of the given adjective or adverb for the sentence.', 'far', 'She lives ____ from school than I do.', 'farther', 'farthest', 'more far', 'most far', 'The comparative form of "far" in distance is "farther".');
