INSERT INTO `verbal` (`category`, `type`, `word`, `question`, `correct_answer`, `wrong_answer1`, `wrong_answer2`, `wrong_answer3`, `explanation`) VALUES
('Grammar and Structure', 'Superlative Form', 'high', 'Mount Everest is ______ peak in the world.', 'the highest', 'higher', 'the most high', 'more high', 'Rule: Short adjectives (1-2 syllables) add "-est" with "the". "High" → "highest"'),
('Grammar and Structure', 'Superlative Form', 'intelligent', 'She is ______ student in our class.', 'the most intelligent', 'the intelligenter', 'the intelligentest', 'most intelligent', 'Rule: Adjectives with ≥3 syllables use "the most" + base form'),
('Grammar and Structure', 'Superlative Form', 'hot', 'July is ______ month in the Philippines.', 'the hottest', 'the hotter', 'the most hot', 'hottest', 'Rule: CVC-ending words double final consonant + "-est" (hot → hottest)'),
('Grammar and Structure', 'Superlative Form', 'bad', 'That was ______ movie I’ve ever seen.', 'the worst', 'the baddest', 'the worse', 'the most bad', 'Rule: Irregular form of "bad" is "worst" (always with "the")'),
('Grammar and Structure', 'Superlative Form', 'good', 'This is ______ solution for the problem.', 'the best', 'the goodest', 'the better', 'the most good', 'Rule: Irregular superlative of "good" is "best"'),
('Grammar and Structure', 'Superlative Form', 'challenging', 'The final level was ______ part of the game.', 'the most challenging', 'challenginger', 'the challengest', 'the challengingest', 'Rule: Multi-syllable adjectives use "the most" + adjective'),
('Grammar and Structure', 'Superlative Form', 'far', 'Pluto is ______ planet from the sun.', 'the farthest', 'the farest', 'the furthiest', 'the most far', 'Rule: "Far" has irregular superlatives: farthest/furthest'),
('Grammar and Structure', 'Superlative Form', 'little', 'This bracelet costs ______ amount among the options.', 'the least', 'the littlest', 'the lesser', 'the most little', 'Rule: Superlative of "little" (quantity) is "least"'),
('Grammar and Structure', 'Superlative Form', 'efficient', 'Solar power is ______ energy source.', 'the most efficient', 'the efficientest', 'the most efficiency', 'efficientest', 'Rule: 3+-syllable adjectives require "the most" + base form'),
('Grammar and Structure', 'Superlative Form', 'simple', 'This is ______ method to calculate.', 'the simplest', 'the most simple', 'the simpler', 'the simpliest', 'Rule: 2-syllable adjectives ending in "-le" drop "e" + "-st"');
INSERT INTO `verbal` (`category`, `type`, `word`, `question`, `correct_answer`, `wrong_answer1`, `wrong_answer2`, `wrong_answer3`, `explanation`) VALUES
('Grammar and Structure', 'Superlative Form', 'expensive', 'This is ______ restaurant in the city.', 'the most expensive', 'expensivest', 'the expensiviest', 'most expensive', 'Rule: Adjectives ≥3 syllables use "the most" + base form (expensive → most expensive)'),
('Grammar and Structure', 'Superlative Form', 'tiny', 'The hummingbird is ______ bird species.', 'the tiniest', 'the most tiny', 'the tinier', 'tinyest', 'Rule: Two-syllable adjectives ending in -y change to -i + -est (tiny → tiniest)'),
('Grammar and Structure', 'Superlative Form', 'far', 'Alpha Centauri is ______ star system visible to the naked eye.', 'the farthest', 'the farest', 'the most far', 'farthest', 'Rule: Irregular form of "far" is "farthest" or "furthest" (always with "the")'),
('Grammar and Structure', 'Superlative Form', 'simple', 'This ______ solution is surprisingly effective.', 'simplest', 'most simple', 'more simple', 'simpliest', 'Rule: Two-syllable adjectives ending in -le drop -e + -st (simple → simplest)'),
('Grammar and Structure', 'Superlative Form', 'dangerous', 'Skydiving is ______ activity on their list.', 'the most dangerous', 'dangerousest', 'the dangerousest', 'most danger', 'Rule: Adjectives ≥3 syllables require "the most" + base form'),
('Grammar and Structure', 'Superlative Form', 'little', 'This project requires ______ funding among all proposals.', 'the least', 'the littlest', 'the lesser', 'the most little', 'Rule: Superlative of "little" (quantity) is "least"'),
('Grammar and Structure', 'Superlative Form', 'big', 'The blue whale is ______ animal on Earth.', 'the biggest', 'the most big', 'the bigest', 'biggest', 'Rule: CVC-ending words double final consonant + -est (big → biggest)'),
('Grammar and Structure', 'Superlative Form', 'bad', 'The typhoon caused ______ damage in coastal areas.', 'the worst', 'the baddest', 'the worse', 'the most bad', 'Rule: Irregular form of "bad" is "worst"'),
('Grammar and Structure', 'Superlative Form', 'efficient', 'Electric vehicles are ______ transportation method.', 'the most efficient', 'the efficientest', 'the most efficiency', 'efficientest', 'Rule: 3+-syllable adjectives require "the most" + base form'),
('Grammar and Structure', 'Superlative Form', 'good', 'This is ______ approach to solve the problem.', 'the best', 'the goodest', 'the better', 'the most good', 'Rule: Irregular superlative of "good" is "best"');
INSERT INTO `verbal` (`category`, `type`, `word`, `question`, `correct_answer`, `wrong_answer1`, `wrong_answer2`, `wrong_answer3`, `explanation`) VALUES
('Grammar and Structure', 'Superlative Form', 'cold', 'Antarctica experiences ______ temperatures on Earth.', 'the coldest', 'colder', 'most coldest', 'coldest', 'Rule: 1-syllable adjectives add "-est" with "the". CVC words double final consonant if preceded by single vowel (cold → coldest).'),
('Grammar and Structure', 'Superlative Form', 'important', 'This is ______ decision you''ll make this year.', 'the most important', 'the importanter', 'the importantest', 'most important', 'Rule: Adjectives with ≥3 syllables use "the most" + base form.'),
('Grammar and Structure', 'Superlative Form', 'funny', 'That was ______ joke I''ve ever heard.', 'the funniest', 'the funnier', 'the most funny', 'funnyest', 'Rule: Adjectives ending in -y change to -i + -est (funny → funniest).'),
('Grammar and Structure', 'Superlative Form', 'many', 'She has ______ experience among the candidates.', 'the most', 'the manyest', 'the more', 'the much', 'Rule: "Many" has irregular superlative form "most" when referring to quantity.'),
('Grammar and Structure', 'Superlative Form', 'narrow', 'This path is ______ route through the canyon.', 'the narrowest', 'the most narrow', 'the more narrow', 'narrowest', 'Rule: Two-syllable adjectives ending in -ow typically use -est (narrow → narrowest).'),
('Grammar and Structure', 'Superlative Form', 'beautiful', 'The orchid is ______ flower in the exhibit.', 'the most beautiful', 'beautifullest', 'the beautifulest', 'most beautiful', 'Rule: 3+-syllable adjectives require "the most" + base form.'),
('Grammar and Structure', 'Superlative Form', 'little', 'This option requires ______ investment capital.', 'the least', 'the littlest', 'the lesser', 'the most little', 'Rule: "Little" (quantity) has irregular superlative "least".'),
('Grammar and Structure', 'Superlative Form', 'smart', 'He is ______ student in the advanced class.', 'the smartest', 'the most smart', 'the smarter', 'smartest', 'Rule: 1-syllable adjectives add "-est" with "the".'),
('Grammar and Structure', 'Superlative Form', 'delicious', 'Grandma''s pie is ______ dessert at the fair.', 'the most delicious', 'the deliciousest', 'deliciousest', 'most delicious', 'Rule: Adjectives with ≥3 syllables use "the most" + base form.'),
('Grammar and Structure', 'Superlative Form', 'far', 'This is ______ I''ve ever traveled from home.', 'the farthest', 'the furthest', 'the farest', 'the most far', 'Rule: "Far" has two accepted irregular superlatives: farthest (physical distance) and furthest (abstract concepts).');
