INSERT INTO `verbal` 
(`category`, `type`, `sub_type`, `instruction`, `word`, `question`, `correct_answer`, `wrong_answer1`, `wrong_answer2`, `wrong_answer3`, `explanation`) VALUES
('Grammar and Structure', 'Tense and Conjugation', 'Present Simple', 'Choose the correct tense or conjugated form of the given verb for the sentence.', 'run', 'She ____ every morning before work.', 'runs', 'run', 'running', 'ran', 'In the third person singular (she), present simple adds -s: runs.'),
('Grammar and Structure', 'Tense and Conjugation', 'Past Simple', 'Choose the correct tense or conjugated form of the given verb for the sentence.', 'eat', 'Yesterday, they ____ lunch at a new restaurant.', 'ate', 'eated', 'eats', 'eating', 'The past simple form of "eat" is ate.'),
('Grammar and Structure', 'Tense and Conjugation', 'Future Simple', 'Choose the correct tense or conjugated form of the given verb for the sentence.', 'go', 'We ____ to the beach tomorrow.', 'will go', 'go', 'going', 'went', 'Future simple is formed with "will" + base verb: will go.'),
('Grammar and Structure', 'Tense and Conjugation', 'Present Continuous', 'Choose the correct tense or conjugated form of the given verb for the sentence.', 'study', 'He ____ for his exams right now.', 'is studying', 'studies', 'study', 'was studying', 'Present continuous is formed with is/are + verb-ing: is studying.'),
('Grammar and Structure', 'Tense and Conjugation', 'Past Continuous', 'Choose the correct tense or conjugated form of the given verb for the sentence.', 'sleep', 'They ____ when the phone rang.', 'were sleeping', 'slept', 'sleep', 'are sleeping', 'Past continuous is was/were + verb-ing: were sleeping.'),
('Grammar and Structure', 'Tense and Conjugation', 'Present Perfect', 'Choose the correct tense or conjugated form of the given verb for the sentence.', 'finish', 'I ____ my homework already.', 'have finished', 'finished', 'finishing', 'finish', 'Present perfect is have/has + past participle: have finished.');
