INSERT INTO `numerical` (`id`, `category`, `type`, `word`, `question`, `image`, `correct_answer`, `wrong_answer1`, `wrong_answer2`, `wrong_answer3`, `explanation`, `created_at`) VALUES
(1, 'Foundations and Basics', 'Variables and Expressions', 'Expression', 'Which of the following is a correct algebraic expression for \"5 more than a number x\"?', NULL, 'x + 5', '5x', 'x - 5', '5 - x', '“5 more than a number x” means x + 5.', '2025-06-30 06:08:15'),
(2, 'Foundations and Basics', 'Variables and Expressions', 'Variable', 'In the expression 3x + 4, what does \"x\" represent?', NULL, 'A variable', 'A constant', 'An exponent', 'A coefficient', '“x” is a variable — it can change or represent unknown values.', '2025-06-30 06:08:15'),
(3, 'Foundations and Basics', 'Variables and Expressions', 'Coefficient', 'What is the coefficient of x in the expression 7x - 2?', NULL, '7', '-2', 'x', '9', 'The coefficient is the number multiplied by the variable, which is 7.', '2025-06-30 06:08:15'),
(4, 'Foundations and Basics', 'Variables and Expressions', 'Simplify', 'Simplify the expression: 2x + 3x', NULL, '5x', '6x', 'x^2', '10x', '2x + 3x = 5x because like terms are combined.', '2025-06-30 06:08:15'),
(5, 'Foundations and Basics', 'Variables and Expressions', 'Evaluate', 'Evaluate the expression 4x when x = 3.', NULL, '12', '7', '1', '43', 'Substitute 3 into x: 4 * 3 = 12.', '2025-06-30 06:08:15');
